import React, { useEffect, useState } from 'react'
import API_SERVICES from '../../services/api_services'
import "./HomeBanner.css"

const HomeBanner = () => {
    const [movie, setMovie] = useState<{backdrop_path:string,overview:string}>({backdrop_path:String(null),overview:String(null)})
    const base_Url = "https://image.tmdb.org/t/p/original/"
    useEffect(() => {
        async function fetchMovieData() {
            const request = await API_SERVICES.fetchNetflixOriginals()
            setMovie(
                request.data.results[
                Math.floor(Math.random() * request.data.results.length - 1)
                ]
            )
            return request;
        }
        fetchMovieData()
    }, [])

    const truncate = (string: string, n: number) => {
        return string?.length > n ? string.substring(0, n - 1) + '...' : string;
    }

    return (
        <div className='homeBanner' style={{
            backgroundSize: "cover",
            backgroundPosition: "center",
            backgroundImage: `url('${base_Url}${movie?.backdrop_path}')`
        }}>
            <div className='bannerContent'>
                <div className="bannerContentButtons">
                    <button className="bannerButton">Play</button>
                    <button className="bannerButton">Next</button>
                </div>

                <div className="bannerContentText">
                truncate(`${movie?.overview}`,150)
                </div>

            </div>

        </div>
    )
}

export default HomeBanner
