import React, { FC, useCallback, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Movie from '../../components/Movie/Movie'
import API_SERVICES from '../../services/api_services'
import { MOVIE } from '../../services/interfaces'
import "./Details.css"

const Details:FC = () => {
  const p:any = useParams<any>()
  const [movie,setMovie] = useState<MOVIE>({ id:0,
    original_name:"",
    title :"",
    name:"",
    backdrop_path: "",
    overview: "" })


  const getMoviewDetailFromId  = useCallback(async () => {
    let {data} = await API_SERVICES.fetchDetails(p.id)
    if(data) {
      setMovie(data)
    }
  },[])

  useEffect(() => {
    getMoviewDetailFromId()
  },[getMoviewDetailFromId])
  return (
    <div className='details'>
      <Movie movie={movie}/>
    </div>
  )
}

export default Details
